import { createSlice } from "@reduxjs/toolkit"
import { PETS } from "../../data/data"

export const flastatSlice = createSlice({
    name: "Flastat",
    initialState: PETS.Flastat,
    reducers: {}
});

export const selectFlastat = (state) => state.flastat;
